import React, { useState } from "react";
import ReactDOM from "react-dom";
import Audionav from "./Audionav.jsx";
var prevAudio;
function Createmusic(props) {
  var [currentAudio, setAudio] = useState("");
  function playSong(path) {
    currentAudio = new Audio(props.path);
    currentAudio.play();
    var duration=currentAudio.duration;
    console.log(duration);
    setAudio(() => {
      if (prevAudio) {
        console.log("works");
        prevAudio.pause();
      }
      prevAudio = currentAudio;
    });
    ReactDOM.render(
      <Audionav text={props.text} />,
      document.getElementById("audiona")
    );
  }

  return (
    <div className="music">
      <p>{props.text}</p>
      <button onClick={playSong}>
        <img src="./Icons/play.png" />
      </button>
    </div>
  );
}
export default Createmusic;
